(function(n){n.addEventListener("mouseup",function(t){if(t.target){let i=t.target.closestClass("dropdown-trigger");if(i!==null)i.parentNode.classList.toggle("active");else{let i=n.body.querySelectorAll(".dropdown");[].forEach.call(i,function(n){n===t.target||n.contains(t.target)||n.classList.remove("active")})}}})})(document,window,0);