function searchAttendance(){let n=new modal.get(manageAttendance);if(n!==null){let i=new FormData;i.append("id",n.trigger.dataset.id);i.append("date",n.trigger.dataset.date);let t=new XMLHttpRequest;t.open("POST","/Attendance/SearchAttendance/");t.onload=function(){if(t.status===200){let i=JSON.parse(t.response);i.message!==undefined&&(n.setHeader(i.header),n.setContent(i.content),manageAttendanceForm=document.getElementById("manage_attendance_form"),manageAttendanceFeedback=document.getElementById("manage_attendance_feedback"))}};feedbackMessage.innerHTML="";t.send(i)}}function resetInformation(){manageAttendanceFeedback.innerHTML=""}function manageAttendanceSuccess(n){if(n.success){let t=new modal.get(manageAttendance);manageAttendanceForm.reset();feedbackMessage.innerHTML=n.feedback;t.close()}else manageAttendanceFeedback.innerHTML=n.feedback}let feedbackMessage=null,manageAttendance=null,manageAttendanceForm=null,manageAttendanceFeedback=null;(function(){feedbackMessage=document.getElementById("feedback_message");manageAttendance=document.getElementById("manage_attendance");new modal.init(manageAttendance,{onClose:resetInformation,beforeOpen:searchAttendance})})();