(function(){document.body.addEventListener("mousedown",function(n){if(n.target){let t=n.target.closestClass("navigation-stepper");if(t){let i=n.target.closestClass("nav-step");if(i)if(i.classList.contains("active"))t.querySelectorAll(".active").forEach(function(n){n.classList.remove("active")}),i.classList.add("active");else{let n=document.getElementById(i.dataset.target);n&&n.scrollIntoView({behavior:"smooth",block:"start",inline:"start"})}}}});window.addEventListener("scroll",function(){let n=document.getElementById("navigation_stepper").querySelectorAll(".nav-step"),t=[].slice.call(n).reverse();t.forEach(function(t){let i=document.getElementById(t.dataset.target);if(i&&i.isInViewport()){for(var r of n)r.classList.remove("active");return t.classList.add("active"),!1}})},!1);document.getElementById("contact").addEventListener("mousedown",function(){copyToClipboard(this.textContent);createSnackbar(this.dataset.message)})})();